import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <>
      {/* Main container */}
      <div style={styles.container}>
        {/* Left Side (Menu) */}
        <div style={styles.leftSide}>
          <header style={styles.header}>
            <div style={styles.logo}>โลโก้</div>
            <nav style={styles.nav}>
              <Link to="/" style={styles.navItem}>ประวัติ</Link>
              <Link to="/login" style={styles.navItem}>วิธีทำงาน</Link>
              <Link to="/register" style={styles.navItem}>บุคลากร</Link>
              <Link to="/services" style={styles.navItem}>กิจกรรม</Link>
              <Link to="/goal" style={styles.navItem}>เป้าหมาย</Link>
            </nav>
          </header>
        </div>

        {/* Right Side (Main content) */}
        <div style={styles.rightSide}>
          {/* Main content (Form and image) */}
          <section style={styles.mainSection}>
            <h3 style={styles.sectionTitle}>ข้อมูล</h3>
            <input type="text" placeholder="ชื่อหน่วยงาน" style={styles.inputField} />
            <textarea placeholder="รายละเอียด" style={styles.textArea}></textarea>
            <div style={styles.imageBox}></div> {/* Placeholder for image */}
          </section>
        </div>
      </div>

      {/* Footer */}
      <footer style={styles.footer}>
        <p>© 2025 studentvolunteer. All Rights Reserved.</p>
      </footer>
    </>
  );
};

const styles = {
  container: {
    display: 'flex',
    height: '100vh',
  },
  leftSide: {
    width: '250px', // ขนาดของฝั่งซ้าย
    backgroundColor: '#a8d0e6', // สีพื้นหลังของฝั่งซ้าย
    padding: '20px',
  },
  header: {
    marginBottom: '40px',
  },
  logo: {
    fontSize: '24px',
    fontWeight: 'bold',
  },
  nav: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  navItem: {
    color: '#0077b6',
    textDecoration: 'none',
    fontSize: '16px',
  },
  rightSide: {
    flex: 1, // ฝั่งขวาจะยืดตามขนาดที่เหลือ
    padding: '20px',
    overflowY: 'scroll', // หากเนื้อหายาวเกินไป สามารถเลื่อนดูได้
  },
  mainSection: {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  },
  sectionTitle: {
    fontSize: '24px',
    marginBottom: '20px',
    color: '#0077b6',
  },
  inputField: {
    width: '100%',
    padding: '10px',
    fontSize: '16px',
    marginBottom: '20px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  textArea: {
    width: '100%',
    padding: '10px',
    fontSize: '16px',
    height: '100px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    marginBottom: '20px',
  },
  imageBox: {
    height: '150px',
    backgroundColor: '#d0e6f7',
    borderRadius: '8px',
    border: '2px solid #0077b6',
  },
  footer: {
    backgroundColor: '#003366',
    color: 'white',
    textAlign: 'center',
    padding: '20px',
    marginTop: '40px',
  },
};

export default Home;
