import React from 'react';
import { Link } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const styles = {
    container: {
      display: 'flex', // ใช้ flexbox เพื่อแบ่งหน้าจอ
      height: '100vh', // ให้สูงเต็มหน้าจอ
    },
    leftSide: {
      flex: 1.5, // ฝั่งซ้ายจะใช้พื้นที่ 50%
      backgroundImage: 'url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcThDqyqKmVjYDDAlMbyYG-IRVLM64vI2_pH0Q&s)', // ใส่ URL ของภาพพื้นหลังที่ต้องการ
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    },
    rightSide: {
      flex: .7, // ฝั่งขวาจะใช้พื้นที่ 50%
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'white', // หรือสีอื่นๆ ตามที่ต้องการ
    },
    form: {
      backgroundColor: 'transparent', // ไม่มีพื้นหลังขาว
      padding: '30px 50px',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      width: '100%',
      maxWidth: '400px',
      textAlign: 'center',
    },
    buttonStyle: {
      backgroundColor: '#4285F4',
      color: 'white',
      border: 'none',
      padding: '15px',
      width: '100%',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '16px',
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftSide}></div> {/* ฝั่งซ้ายมีแค่ภาพ */}
      <div style={styles.rightSide}>
        <div style={styles.form}>
          <h2>เข้าสู่ระบบ</h2>
          <Link to="/register">
            <button style={styles.buttonStyle}>
              เข้าสู่ระบบด้วยบัญชี Google
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
