import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaArrowLeft, FaHome } from 'react-icons/fa';
import './Profile.css'; // ✅ เพิ่มบรรทัดนี้

function Editprofile() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    studentId: '',
    gender: '',
    phone: '',
    faculty: '',
    birthDate: '',
    major: '',
    email: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    navigate('/profile');
  };

  return (
    <>
      <div className="top-left">
        <Link to="/profile" className="link-button">
          <FaArrowLeft size={40} />
        </Link>
      </div>

      <div className="top-right">
        <Link to="/" className="link-button">
          <FaHome size={40} />
        </Link>
      </div>

      <div className="form-container">
        <h2>แก้ไขโปรไฟล์</h2>
        <form onSubmit={handleSubmit}>
          {[
            { label: 'ชื่อ', name: 'firstName' },
            { label: 'นามสกุล', name: 'lastName' },
            { label: 'รหัสนักศึกษา', name: 'studentId' },
            { label: 'เพศ', name: 'gender' },
            { label: 'เบอร์โทรศัพท์', name: 'phone' },
            { label: 'คณะ', name: 'faculty' },
            { label: 'วันเดือนปีเกิด', name: 'birthDate', type: 'date' },
            { label: 'สาขา', name: 'major' },
            { label: 'อีเมล', name: 'email', type: 'email' },
          ].map(({ label, name, type = 'text' }) => (
            <div key={name} className="input-group">
              <label htmlFor={name} className="label">{label}</label>
              <input
                type={type}
                id={name}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                className="input"
                required
              />
            </div>
          ))}

          <button type="submit" className="button">
            บันทึก
          </button>
        </form>
      </div>
    </>
  );
}

export default Editprofile;
