import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaArrowLeft, FaHome } from 'react-icons/fa';

const styles = {
  topLeft: {
    position: 'fixed',
    top: 20,
    left: 20,
    zIndex: 1,
  },
  topRight: {
    position: 'fixed',
    top: 20,
    right: 20,
    zIndex: 1,
  },
  linkButton: {
    textDecoration: 'none',
    color: 'black',
  },
  formContainer: {
    padding: '60px 20px',
    maxWidth: '500px',
    margin: 'auto',
    textAlign: 'left',
  },
  inputGroup: {
    marginBottom: '15px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
  },
  input: {
    width: '100%',
    padding: '8px',
    fontSize: '16px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: 'royalblue',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

function Profile() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    studentId: '',
    gender: '',
    phone: '',
    faculty: '',
    birthDate: '',
    major: '',
    email: '',
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('บันทึกข้อมูลสำเร็จ!');
    console.log(formData); // หรือส่งไปยัง backend
  };

  return (
    <>
      {/* ปุ่มมุมซ้ายบน */}
      <div style={styles.topLeft}>
        <Link to="/" style={styles.linkButton}>
          <FaArrowLeft size={40} />
        </Link>
      </div>

      {/* ปุ่มมุมขวาบน */}
      <div style={styles.topRight}>
        <Link to="/" style={styles.linkButton}>
          <FaHome size={40} />
        </Link>
      </div>

      {/* ฟอร์มแก้ไขโปรไฟล์ */}
      <div style={styles.formContainer}>
        <h2>แก้ไขโปรไฟล์</h2>
        <form onSubmit={handleSubmit}>
          {[
            { label: 'ชื่อ', name: 'firstName' },
            { label: 'นามสกุล', name: 'lastName' },
            { label: 'รหัสนักศึกษา', name: 'studentId' },
            { label: 'เพศ', name: 'gender' },
            { label: 'เบอร์โทรศัพท์', name: 'phone' },
            { label: 'คณะ', name: 'faculty' },
            { label: 'วันเดือนปีเกิด', name: 'birthDate', type: 'date' },
            { label: 'สาขา', name: 'major' },
            { label: 'อีเมล', name: 'email', type: 'email' },
          ].map(({ label, name, type = 'text' }) => (
            <div key={name} style={styles.inputGroup}>
              <label htmlFor={name} style={styles.label}>{label}</label>
              <input
                type={type}
                id={name}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                style={styles.input}
                required
              />
            </div>
          ))}

          <button type="submit" style={styles.button}>บันทึก</button>
        </form>
      </div>
    </>
  );
}

export default Profile;
