import React from 'react';
import { Link } from 'react-router-dom';
import './Login.css';

// โหลดโลโก้จากไฟล์ที่อัปโหลดมา
import googleLogo from '/mnt/data/f2eafcca-8e6c-43e2-93b5-5c32b8a97116.png';

const Login = () => {
  const styles = {
    container: {
      display: 'flex', // ใช้ flexbox เพื่อแบ่งหน้าจอ
      height: '100vh', // ให้สูงเต็มหน้าจอ
    },
    leftSide: {
      flex: 0.6,
      backgroundImage: 'url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcThDqyqKmVjYDDAlMbyYG-IRVLM64vI2_pH0Q&s)', // ใส่ URL ของภาพพื้นหลังที่ต้องการ
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    },
    rightSide: {
      flex: 0.4,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'white',
    },
    form: {
      backgroundColor: 'transparent',
      padding: '30px 50px',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(255, 255, 255, 0.1)',
      width: '100%',
      maxWidth: '400px',
      textAlign: 'center',
    },
    buttonStyle: {
      backgroundColor: '#4285F4',
      color: 'white',
      border: 'none',
      padding: '15px',
      width: '100%',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '16px',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    iconStyle: {
      width: '20px',
      height: '20px',
      marginRight: '10px', // ช่องว่างระหว่างโลโก้และข้อความ
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftSide}></div>
      <div style={styles.rightSide}>
        <div style={styles.form}>
          <h2>เข้าสู่ระบบ</h2>
          <Link to="/register">
            <button style={styles.buttonStyle}>
              <img src={googleLogo} alt="Google logo" style={styles.iconStyle} />
              เข้าสู่ระบบด้วยบัญชี Google
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
