import React from 'react';
import { Link } from 'react-router-dom';
import { FaArrowLeft, FaHome } from 'react-icons/fa';
import './Login.css';

const Login = () => {
  const styles = {
    topLeft: {
      position: 'fixed',
      top: 20,
      left: 20,
      zIndex: 1, // ลดให้ต่ำกว่ากล่องฟอร์ม
    },
    topRight: {
      position: 'fixed',
      top: 20,
      right: 20,
      zIndex: 1,
    },
    linkButton: {
      textDecoration: 'none',
      color: 'black',
    },
    containerStyle: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#FFB5A7', // สีพื้นหลังปกติ
      padding: '0 20px',
    },
    formStyle: {
      backgroundColor: 'white',
      padding: '30px 50px',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      width: '100%',
      maxWidth: '400px',
      textAlign: 'center',
    },
    buttonStyle: {
      backgroundColor: '#4285F4',
      color: 'white',
      border: 'none',
      padding: '15px',
      width: '100%',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '16px',
    },
  };

  return (
    <div style={styles.containerStyle}>
      <div style={styles.formStyle}>
        <h2>เข้าสู่ระบบ</h2>
        <Link to="/register">
          <button style={styles.buttonStyle}>
            เข้าสู่ระบบด้วยบัญชี Google
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Login;
